mapper.select:
SELECT B.board_id AS "BoardId", B.title AS "BoardTitle", B.contents AS "BoardContents", B.hit AS "BoardHit", B.created_date AS "BoardCreatedDate", B.updated_date AS "BoardUpdatedDate", B.user_id AS "UserId",B.email AS "UserEmail", B.nickname AS "UserNickName", B.category_id AS "CategoryId", B.name AS "CategoryName", CM.comment_id AS "CommentId" FROM ( SELECT B.board_id, B.title, B.contents, B.hit, B.created_date , B.updated_date, B.user_id, B.category_id, U.email, U.nickname, C.name FROM Board AS B LEFT OUTER JOIN User AS U on (B.user_id = U.user_id) LEFT OUTER JOIN category AS C on (B.category_id = C.category_id)
ORDER BY B.board_id desc
) AS B LEFT OUTER JOIN comment AS CM on (B.board_id = CM.board_id)

SELECT B.board_id AS "BoardId", B.title AS "BoardTitle", B.contents AS "BoardContents", B.hit AS "BoardHit", B.created_date AS "BoardCreatedDate", B.updated_date AS "BoardUpdatedDate", B.user_id AS "UserId",B.email AS "UserEmail", B.nickname AS "UserNickName", B.category_id AS "CategoryId", B.name AS "CategoryName", CM.comment_id AS "CommentId" FROM ( SELECT B.board_id, B.title, B.contents, B.hit, B.created_date , B.updated_date, B.user_id, B.category_id, U.email, U.nickname, C.name FROM Board AS B LEFT OUTER JOIN User AS U on (B.user_id = U.user_id) LEFT OUTER JOIN category AS C on (B.category_id = C.category_id) WHERE B.category_id = #{categoryId}
ORDER BY B.board_id desc
) AS B LEFT OUTER JOIN comment AS CM on (B.board_id = CM.board_id)

SELECT B.board_id AS "BoardId", B.title AS "BoardTitle", B.contents AS "BoardContents", B.hit AS "BoardHit", B.created_date AS "BoardCreatedDate", B.updated_date AS "BoardUpdatedDate", B.user_id AS "UserId",B.email AS "UserEmail", B.nickname AS "UserNickName", B.category_id AS "CategoryId", B.name AS "CategoryName", CM.comment_id AS "CommentId" FROM ( SELECT B.board_id, B.title, B.contents, B.hit, B.created_date , B.updated_date, B.user_id, B.category_id, U.email, U.nickname, C.name FROM Board AS B LEFT OUTER JOIN User AS U on (B.user_id = U.user_id) LEFT OUTER JOIN category AS C on (B.category_id = C.category_id) WHERE B.user_id = #{userId}
ORDER BY B.board_id desc
) AS B LEFT OUTER JOIN comment AS CM on (B.board_id = CM.board_id)
